import React from 'react';

class Counter extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            counter: 0,
            multiplier: 1
        };
    }

    increment = () => {
        let multiplier = this.state.multiplier;
        multiplier = Number(multiplier);
        this.setState({counter: this.state.counter + multiplier});
    }

    decrement = () => {
        let multiplier = this.state.multiplier;
        multiplier = Number(multiplier);
        this.setState({counter: this.state.counter - multiplier});
    }

    setMultiplier = (e) => {
        this.setState({multiplier:e.target.value});
        console.log(this.state.multiplier);
        e.preventDefault();
    }

    render() {
        return (
            <div class = 'container'>
                <div class = 'card'>
                    <h1>{this.state.counter}</h1>
                    <select value = {this.state.multiplier}
                        onChange = {this.setMultiplier}>
                        <option value = '1'>1</option>
                        <option value = '5'>5</option>
                        <option value = '10'>10</option>
                    </select>

                    <br/>

                    <button onClick = {this.increment}>Increment</button>
                    <button onClick = {this.decrement}>Decrement</button>
                </div>
            </div>
        );
    }
}

export default Counter;
